insert  into `core_email_templates`(`email_template_id`,`name`,`subject`,`body`,`active`,`note`) values (1,'Contact Information','Contact Information','<p>Dear Admin,</p>\r\n<p>Customer wrote contact below. <br />\r\n<table cellspacing=\"\\&quot;0\\&quot;\" cellpadding=\"\\&quot;5\\&quot;\" border-collapse:=\"\" solid=\"\" style=\"\">\r\n    {row}\r\n    <tbody>\r\n        <tr>\r\n            <td border-collapse:=\"\" solid=\"\" style=\"\">{field}:</td>\r\n            <td border-collapse:=\"\" solid=\"\" style=\"\">{value}</td>\r\n        </tr>\r\n        {/row}-\r\n    </tbody>\r\n</table>\r\n</p>\r\n<p>Best Regards.</p>\r\n<p>{website_title} Team</p>',0,''),(3,'Client Order Notification','Client Order Notification','<p>Hello {firstname},</p>\r\n<p>You have registered as a member with Bennu Collective.<br />\r\nThis e-mail contains your log in details:<br />\r\n<table border=\"1\" cellspacing=\"3\" cellpadding=\"0\">\r\n    <tbody>\r\n        <tr>\r\n            <td width=\"150\">Full Name</td>\r\n            <td>{fullname}</td>\r\n        </tr>\r\n        <tr>\r\n            <td>Email</td>\r\n            <td>{email}</td>\r\n        </tr>\r\n        <tr>\r\n            <td>Password</td>\r\n            <td>{password}</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\nPlease do not respond to this message as it is automatically generated and is for information purposes only</p>',1,''),(2,'Client Order Notification','Client Order Notification','Hello {firstname}, <br />\nYour new password is {password}.\n<br /><br />\nThe Bennu Collective<br>\n<a href=\"http://bennucollective.com\">www.bennucollective.com</a>',1,''),(4,'Client Order Notification','Client Order Notification','Order Number: #{oid}\n<table cellspacing=\"0\" cellpadding=\"0\" border=\"1\">\n    <tbody>\n        <tr>\n            <td width=\"420\">Product(s)</td>\n            <td width=\"75\">Price</td>\n            <td width=\"75\">Quantity</td>\n            <td width=\"85\" align=\"center\">Total</td>\n        </tr>\n        {row}\n        <tr>\n            <td width=\"420\">\n            <table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\n                <tbody>\n                    <tr>\n                        <td width=\"107\" valign=\"top\"><u>Image</u>: <img width=\"40\" align=\"top\" style=\"margin: 0px;\" src=\"{thumbnail}\" alt=\"\" /></td>\n                        <td width=\"135\" valign=\"top\"><u>Code</u>: {product_serial} <br />\n                        <u>Name</u>: {product_name} <br />\n                        <u>Attributes</u>: <br />\n                        {attributes}</td>\n                    </tr>\n                </tbody>\n            </table>\n            </td>\n            <td>${net_price}</td>\n            <td width=\"80\" align=\"center\">{quantity}</td>\n            <td>${price}</td>\n        </tr>\n        {/row}\n        <tr>\n            <td align=\"right\" colspan=\"3\">Gift Card Number</td>\n            <td width=\"75\">#{coupon}</td>\n        </tr>\n        <tr>\n            <td align=\"right\" colspan=\"3\">Amount of gift card redeemed:</td>\n            <td width=\"75\">${fee}</td>\n        </tr>\n        <tr>\n            <td align=\"right\" colspan=\"3\">Total amount left on gift card:</td>\n            <td width=\"75\">${amount2}</td>\n        </tr>\n        <tr>\n            <td align=\"right\" colspan=\"3\">Subtotal&nbsp;</td>\n            <td width=\"75\">${subtotal}</td>\n        </tr>\n        <tr>\n            <td align=\"right\" colspan=\"3\">Freight&nbsp;</td>\n            <td width=\"75\">${freight}</td>\n        </tr>\n        <tr>\n            <td align=\"right\" colspan=\"3\">GST&nbsp;</td>\n            <td width=\"75\">${gst}</td>\n        </tr>\n        <tr>\n            <td align=\"right\" colspan=\"3\"><strong>Total</strong>&nbsp;</td>\n            <td width=\"75\">${total}</td>\n        </tr>\n    </tbody>\n</table>\n<br />\n<br />\nKind regards,<br />\nThe Bennu Collective<br />\n<a href=\"http://bennucollective.com\">www.bennucollective.com</a>',1,''),(5,'Client Order Notification','Client Order Notification','<p>Dear {customer_name},</p>\n<p>Thank you for purchasing. The following is the details:</p>\n<p>{order_details}</p>\n<p>&nbsp;</p>\n<p>{customer_details}</p>\n<p>&nbsp;</p>\n<p>Regards,</p>\n<p>{site_title} Team.</p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>',1,''),(6,'admin\\_order\\_notification','Admin Order Notification','<p>Dear Admin,</p>\r\n<p>A new order has been made on your website. The following is the details:</p>\r\n<p>{order_details}</p>\r\n<p>{customer_details}</p>\r\n<p>Regards,</p>\r\n<p>{site_title} Team.</p>',1,''),(7,'contac\\_email\\_test','Contact Email Test Email Choi','<p>Hello {firstname},</p>\r\n<p>You have registered as a member with Bennu Collective.<br />\r\nThis e-mail contains your log in details:</p>\r\n<p><br />\r\n<table border=\"\\&quot;\\\\&quot;\\\\\\\\&quot;1\\\\\\\\&quot;\\\\&quot;\\&quot;\" cellspacing=\"\\&quot;\\\\&quot;\\\\\\\\&quot;3\\\\\\\\&quot;\\\\&quot;\\&quot;\" cellpadding=\"\\&quot;\\\\&quot;\\\\\\\\&quot;0\\\\\\\\&quot;\\\\&quot;\\&quot;\">\r\n    <tbody>\r\n        <tr>\r\n            <td>Full Name</td>\r\n            <td>{fullname}</td>\r\n        </tr>\r\n        <tr>\r\n            <td>Email</td>\r\n            <td>{email}</td>\r\n        </tr>\r\n        <tr>\r\n            <td>Password</td>\r\n            <td>{password}</td>\r\n        </tr>\r\n    </tbody>\r\n</table>\r\n<br />\r\nPlease do not respond to this message as it is automatically generated and is for information purposes only</p>\r\n<p>Please do not respond to this message as it is automatically generated and is for information purposes only</p>',1,'bui van tien duc\r\nbui van tien duc'),(8,'Test Hang','Test Hang','<p>Test thu coi choi</p>',1,''),(10,'Test Them 1 Hang','Test Them 1 Hang','<p>Test Them 1 Hang</p>',1,'');

